    compareAsc(a, b) {
        // Use toUpperCase() to ignore character casing
        const recipeA = a.strMeal ? a.strMeal.toUpperCase() : a.name.toUpperCase();
        const recipeB = b.strMeal ? b.strMeal.toUpperCase() : b.name.toUpperCase();

        let comparison = 0;
        if (recipeA > recipeB) {
            comparison = 1;
        } else if (recipeA < recipeB) {
            comparison = -1;
        }
        return comparison;
    }

    compareDesc(a, b) {
        // Use toUpperCase() to ignore character casing
        const recipeA = a.strMeal ? a.strMeal.toUpperCase() : a.name.toUpperCase();
        const recipeB = b.strMeal ? b.strMeal.toUpperCase() : b.name.toUpperCase();

        let comparison = 0;
        if (recipeA > recipeB) {
            comparison = 1;
        } else if (recipeA < recipeB) {
            comparison = -1;
        }
        return comparison * -1;
    }

